export function encode(data) {
  return data.reduce(function (str, byte) {
    return str + byte.toString(16).padStart(2, "0");
  }, "0x");
}
export function decode(data) {
  if (data.indexOf("0x") === 0) {
    data = data.substr(2);
  }

  if (data.length % 2 === 1) {
    data = "0" + data;
  }

  var key = data.match(/.{2}/g);

  if (key === null) {
    return Buffer.from([]);
  }

  return Buffer.from(key.map(function (byte) {
    return parseInt(byte, 16);
  }));
}
