import camelCase from "camelcase";
import * as toml from "toml";
import { PublicKey } from "@solana/web3.js";
import { Program } from "./program";
import { isBrowser } from "./utils/common";
var _populatedWorkspace = false;
/**
 * The `workspace` namespace provides a convenience API to automatically
 * search for and deserialize [[Program]] objects defined by compiled IDLs
 * in an Anchor workspace.
 *
 * This API is for Node only.
 */

var workspace = new Proxy({}, {
  get: function get(workspaceCache, programName) {
    if (isBrowser) {
      console.log("Workspaces aren't available in the browser");
      return undefined;
    }

    var fs = require("fs");

    var process = require("process");

    if (!_populatedWorkspace) {
      var path = require("path");

      var projectRoot = process.cwd();

      while (!fs.existsSync(path.join(projectRoot, "Anchor.toml"))) {
        var parentDir = path.dirname(projectRoot);

        if (parentDir === projectRoot) {
          projectRoot = undefined;
        }

        projectRoot = parentDir;
      }

      if (projectRoot === undefined) {
        throw new Error("Could not find workspace root.");
      }

      var idlFolder = "".concat(projectRoot, "/target/idl");

      if (!fs.existsSync(idlFolder)) {
        throw new Error("".concat(idlFolder, " doesn't exist. Did you use \"anchor build\"?"));
      }

      var idlMap = new Map();
      fs.readdirSync(idlFolder).forEach(function (file) {
        var filePath = "".concat(idlFolder, "/").concat(file);
        var idlStr = fs.readFileSync(filePath);
        var idl = JSON.parse(idlStr);
        idlMap.set(idl.name, idl);
        var name = camelCase(idl.name, {
          pascalCase: true
        });

        if (idl.metadata && idl.metadata.address) {
          workspaceCache[name] = new Program(idl, new PublicKey(idl.metadata.address));
        }
      }); // Override the workspace programs if the user put them in the config.

      var anchorToml = toml.parse(fs.readFileSync(path.join(projectRoot, "Anchor.toml"), "utf-8"));
      var clusterId = anchorToml.provider.cluster;

      if (anchorToml.programs && anchorToml.programs[clusterId]) {
        attachWorkspaceOverride(workspaceCache, anchorToml.programs[clusterId], idlMap);
      }

      _populatedWorkspace = true;
    }

    return workspaceCache[programName];
  }
});

function attachWorkspaceOverride(workspaceCache, overrideConfig, idlMap) {
  Object.keys(overrideConfig).forEach(function (programName) {
    var wsProgramName = camelCase(programName, {
      pascalCase: true
    });
    var entry = overrideConfig[programName];
    var overrideAddress = new PublicKey(typeof entry === "string" ? entry : entry.address);
    var idl = idlMap.get(programName);

    if (typeof entry !== "string" && entry.idl) {
      idl = JSON.parse(require("fs").readFileSync(entry.idl, "utf-8"));
    }

    workspaceCache[wsProgramName] = new Program(idl, overrideAddress);
  });
}

export default workspace;
